import React, { useState } from 'react';
import Button from 'react-bootstrap/Button';
import Modal from 'react-bootstrap/Modal';
import { Link } from 'react-router-dom';

function ModalDetailPayment({show, handle, list}) {

  return (
    <>

      {/* <Button onClick={() => setLgShow(true)}>Large modal</Button> */}
      <Modal
        size="lg"
        show={show}
        onHide={handle}
        aria-labelledby="example-modal-sizes-title-lg"
        centered
      >

        <Modal.Body>
        Your payment will be confirmed within 1 x 24 <br/> hour To see orders click &nbsp;
        <Link to={`/payment-approve/${list.id}`}   className="text-decoration-none" >
          Here 
        </Link>
        &nbsp; thank you
        </Modal.Body>
      </Modal>
    </>
  );
}

export default ModalDetailPayment
