// import { useState } from 'react';
import { Button,Form,Container } from 'react-bootstrap';
// import { useNavigate } from 'react-router-dom';
import Footer from './footer';
import NavHeader from './navbar';
import navStyle from './navbar.module.css'
import { useState } from 'react';
import { useMutation, useQuery } from 'react-query';
import { API } from '../config/api';
import { useNavigate } from 'react-router-dom';
// import { useNavigate } from 'react-router-dom';





function AddCountry() {
    // const navigate = useNavigate()
    // let {data: country} = useQuery('countryChache', async() => {
    //     const response = await API.get('/country')
    //     return response.data.data
    // })

    // console.log(country);
    const [input, setInput] = useState({
        name: "",
    })
console.log(input.country);
    const handleChange = (e) =>  {
        setInput({
        ...input,
        [e.target.name] : e.target.value,

        })

    }
    const handleSubmit = useMutation(async(e) => {
        try {
            e.preventDefault()


            const config = {
                headers: {
                  'Content-type': 'multipart/form-data',
                },
              };

            //   const body = JSON.stringify(input)


              const formData = new FormData()
              formData.set('name', input.name)



              //   Inser Data
              const response = await API.post('/country', formData, config)
            //   console.log(response);
            } catch (error) {
                console.log(error);
            }

            navigate("/income-trip")
        }) 


        // console.log(input)
  return (
    <>
    <NavHeader navStyle={navStyle.bgNavbar}/>
    <Container> 
        <div className='shadow p-5 mt-5'>
        <h2 className='pt-5 fw-semibold'>Add Trip</h2>
        <Form className='p-3' onSubmit={(e) => handleSubmit.mutate(e)}>
        <Form.Group className="mb-3" controlId="formBasicEmail">
            <Form.Label>Name Country</Form.Label>
            <Form.Control  name="name" type="text" placeholder="Enter text"  value={input.name} onChange={handleChange} />
        </Form.Group>
        <div className='text-center pt-3'>
        <Button style={{padding:'6px 90px'}} className='btn-yellow' type="submit">
            Submit
        </Button>
        </div>
        </Form>
        </div>
    </Container>
    <Footer/>
    </>
  );
}

export default AddCountry
